<?phprequire_once('../conexao.php');session_start();if (!isset($_SESSION['usuarioNome']) or !isset($_SESSION['usuarioId']) or !isset ($_SESSION['usuarioNiveisAcessoId']) or !isset($_SESSION['usuarioEmail'])){    unset(        $_SESSION['usuarioId'],        $_SESSION['usuarioNome'],        $_SESSION['usuarioNiveisAcessoId'],        $_SESSION['usuarioEmail']    );    //redirecionar o usuario para a página de login    header("Location: ../index.php");}$nivelLogado = $_SESSION['usuarioNiveisAcessoId'];header("Content-Type: text/html; charset=utf-8", true);$acao = NULL;if (!function_exists ("GetSQLValueString")){    function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "")    {        $theValue = (!get_magic_quotes_gpc()) ? addslashes($theValue) : $theValue;        switch ($theType) {            case "text":                $theValue = ($theValue != "") ? "UPPER('" . $theValue . "')" : "NULL";                break;            case "int":                $theValue = ($theValue != "") ? intval($theValue) : "NULL";                break;            case "date":                if ($theValue != "") {                    $theValue = explode("/", $theValue);                    $theValue = "'" . $theValue[2] . "-" . $theValue[1] . "-" . $theValue[0] . "'";                } else                    $theValue = "NULL";                break;            case "hour":                if ($theValue != "") {                    $theValue = "'" . $theValue . "'";                } else                    $theValue = "NULL";                break;        }        return $theValue;    }}$editFormAction = $_SERVER["PHP_SELF"]; //action na própria páginaif (isset($_SERVER["QUERY_STRING"])) {    $editFormAction .= "?" . htmlentities($_SERVER["QUERY_STRING"]);}if (isset($_SESSION["codEvento"])) {    mysqli_select_db($conn, "emerjco_eventos");    $query_rsBusca = "SELECT evento.codigo, evento.nome, evento.vagas, evento.local, evento.endereco, porta.data, porta.horaInicio, porta.horaFim, porta.cargaHoraria, porta.tipo FROM evento INNER JOIN porta ON porta.codEvento = evento.codigo WHERE evento.codigo = " . $_SESSION["codEvento"];    $rsBusca = mysqli_query($conn, $query_rsBusca) or die(mysqli_error($conn));    $row_rsBusca = mysqli_fetch_assoc($rsBusca);    $totalRows_rsBusca = mysqli_num_rows($rsBusca);}//inclui eventoif ((isset($_POST["hdAcao"])) && ($_POST["hdAcao"] == "inserir")) {    if(($_POST["nome"] == "") || ($_POST["local"] == "") || ($_POST["endereco"] == "") || ($_POST["hdPortas"] == "") ){        echo "<script>                alert('Preencha todos os campos abaixo!');                 window.history.back();                </script>";    }    else {        $insertSQL = sprintf("INSERT INTO evento (codigo, nome, vagas, local, endereco, tipoEvento1, tipoEvento2, tipoEvento3, tipoEvento4, tipoEvento5, foradasede, tema_humanistico, horaFimInscricao) VALUES (%s, %s, %s, %s, %s, '%s', '%s', '%s', '%s', '%s', %s, %s, %s)",            GetSQLValueString($_POST["codigo"], "int"),            trim(GetSQLValueString($_POST["nome"], "text")),            trim(GetSQLValueString($_POST["vagas"], "int")),            trim(GetSQLValueString($_POST["local"], "text")),            trim(GetSQLValueString($_POST["endereco"], "text")),            $_POST["tipoEvento1"],            $_POST["tipoEvento2"],            $_POST["tipoEvento3"],            $_POST["tipoEvento4"],            $_POST["tipoEvento5"],            GetSQLValueString($_POST["foradasede"], "int"), /*novo campo*/            GetSQLValueString($_POST["tema_humanistico"], "int"),/*novo campo*/            GetSQLValueString($_POST["horaFimInscricao"], "hour")); /*novo campo, hora final das inscrições para o evento*/        mysqli_select_db($conn, "emerjco_eventos");        mysqli_autocommit($conn, TRUE);        $Result1 = mysqli_query($conn, $insertSQL) or die(mysqli_error($conn));        // separa as portas        $portasJuntas = ($_POST["hdPortas"]);        for ($i = 0; $i < (strlen($portasJuntas)/46); $i++) {            $insertSQL = sprintf("INSERT INTO porta (codEvento, hora_oab,  data, horaInicio, horaFim, horaFimReal, cargaHoraria, tipo) VALUES (%s, %s, %s, %s, %s, '00:00', %s, %s)",                GetSQLValueString($_POST["codigo"], "int"),                GetSQLValueString($_POST["horaOAB"], "int"),                GetSQLValueString(substr($portasJuntas, ($i*46), 10), "date"),                GetSQLValueString(substr($portasJuntas, ($i*46) + 15, 5), "hour"),                GetSQLValueString(substr($portasJuntas, ($i*46) + 25, 5), "hour"),                GetSQLValueString(substr($portasJuntas, ($i*46) + 35, 5), "hour"),                GetSQLValueString(substr($portasJuntas, ($i*46) + 45, 1), "int"));            mysqli_select_db($conn, "emerjco_eventos");            $Result2 = mysqli_query($conn, $insertSQL) or die(mysqli_error($conn));        }        if (($Result1) && ($Result2)) {            echo ("<script>alert('Evento cadastrado COM Horas OAB');</script>");            mysqli_commit($conn);            $acao = "inserido";        } elseif (($Result1) == ($Result2)) {            echo "Evento cadastrado SEM Horas OAB '00:00'";            mysqli_commit($conn);            $acao = "oab";        } else {            mysqli_rollback($conn);            echo ("Não foi possível cadastrar o evento.");        }        mysqli_autocommit($conn, TRUE);    }}?><!DOCTYPE html><html><head>    <link rel="stylesheet" href="../css/cadastrar_eventos.css">    <link href="https://fonts.googleapis.com/css?family=Bree+Serif&display=swap" rel="stylesheet">    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css">    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.4.1/jquery.min.js"></script>    <script src="../js/funcoes_cadastrar.